package Casting;

public class UpcastingA {
	public void abc (){
		System.out.println("This is Upcasting A for abc");
	}
	
	public static void xyz() {
		System.out.println("This is Upcasting A for xyz ");
	}
	private static void pqr() {
		System.out.println("This is upcasting A for pqr");
	}
	final static void lmn() {
		System.out.println("This is upcasting A for lmn");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		

	}

}
